-- ----------------------------
-- Table structure for attend
-- ----------------------------
DROP TABLE IF EXISTS `attend`;
CREATE TABLE `attend` (
  `memID` int(11) NOT NULL,
  `courseID` int(11) NOT NULL,
  `attendTime` datetime NOT NULL,
  `duration` varchar(5) DEFAULT NULL COMMENT '学习时长（分钟）',
  `mark` int(4) DEFAULT NULL COMMENT '考试成绩',
  `certificateID` int(10) DEFAULT NULL,
  `type` int(2) DEFAULT '1' COMMENT '是否结课1否，0结课',
  PRIMARY KEY (`courseID`,`memID`),
  KEY `memID` (`memID`),
  CONSTRAINT `courseID` FOREIGN KEY (`courseID`) REFERENCES `courses` (`courseID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `memID` FOREIGN KEY (`memID`) REFERENCES `members` (`memberID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

