-- ----------------------------
-- Table structure for certificates
-- ----------------------------
DROP TABLE IF EXISTS `certificates`;
CREATE TABLE `certificates` (
  `certificateID` int(10) NOT NULL AUTO_INCREMENT COMMENT '证书编码',
  `title` varchar(255) DEFAULT NULL COMMENT '证书名字',
  `content` varchar(255) NOT NULL COMMENT '证书内容',
  `score` int(3) DEFAULT NULL,
  `courseIDcer` int(11) NOT NULL COMMENT '所属课程',
  `memIDcer` int(11) NOT NULL COMMENT '会员编码',
  `getTime` datetime DEFAULT NULL COMMENT '获取时间',
  PRIMARY KEY (`certificateID`),
  KEY `menIDcer` (`memIDcer`) USING BTREE,
  KEY `courseIDcer` (`courseIDcer`) USING BTREE,
  CONSTRAINT `courseIDcer` FOREIGN KEY (`courseIDcer`) REFERENCES `courses` (`courseID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `memIDcer` FOREIGN KEY (`memIDcer`) REFERENCES `members` (`memberID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

