-- ----------------------------
-- Table structure for courses
-- ----------------------------
DROP TABLE IF EXISTS `courses`;
CREATE TABLE `courses` (
  `courseID` int(10) NOT NULL AUTO_INCREMENT COMMENT '课程编码',
  `courseName` varchar(30) NOT NULL COMMENT '课程名称',
  `permission` int(2) NOT NULL DEFAULT '0' COMMENT '课程权限（0免费1普通会与2白银会员3黑金会员）',
  `courseType` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '课程分类',
  `introduction` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '课程简介',
  `enterTime1` varchar(50) DEFAULT NULL COMMENT '报名时间，开始时间',
  `enterTime2` varchar(50) DEFAULT NULL COMMENT '报名时间，结束时间',
  `classTime1` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '上课时间，开始时间',
  `classTime2` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '上课时间，结束时间',
  `publishTime` datetime NOT NULL COMMENT '发布时间',
  `updataTime` datetime DEFAULT NULL COMMENT '更新时间',
  `examTime1` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '考试时间',
  `examTime2` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `staffIDcourse` int(12) DEFAULT NULL COMMENT '操作人员',
  `imgURL` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL,
  `courseStatus` int(2) DEFAULT '0' COMMENT '课程状态0报名中1进行中-1已结束',
  `staffUserName` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL,
  PRIMARY KEY (`courseID`),
  KEY `staffIDcourse` (`staffIDcourse`),
  CONSTRAINT `staffIDcourse` FOREIGN KEY (`staffIDcourse`) REFERENCES `staffs` (`staffID`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

