-- ----------------------------
-- Table structure for exams
-- ----------------------------
DROP TABLE IF EXISTS `exams`;
CREATE TABLE `exams` (
  `examID` int(10) NOT NULL AUTO_INCREMENT COMMENT '试卷编码',
  `score` int(3) NOT NULL DEFAULT '100' COMMENT '总分数',
  `examTime` int(2) DEFAULT '0' COMMENT '考试时长',
  `examContent` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '试卷内容',
  `chooseNum` int(3) NOT NULL DEFAULT '0' COMMENT '选择题数目',
  `chooseScore` int(3) NOT NULL DEFAULT '0' COMMENT '选择题分数',
  `chooseAnswer` varchar(255) DEFAULT NULL COMMENT '选择题答案',
  `courseIDexam` int(11) NOT NULL COMMENT '所属课程ID（外键）',
  `staffIDexam` int(11) DEFAULT NULL COMMENT '操作人员',
  `publishTime` datetime DEFAULT NULL,
  `examTitle` varchar(255) DEFAULT NULL,
  `staffUserName` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL,
  `examStatus` int(2) DEFAULT '0' COMMENT '考试状态0未开始1进行中-1已结束',
  `updateTime` datetime DEFAULT NULL,
  PRIMARY KEY (`examID`),
  KEY `examID` (`examID`),
  KEY `courseIDexam` (`courseIDexam`),
  KEY `staffIDexam` (`staffIDexam`),
  CONSTRAINT `courseIDexam` FOREIGN KEY (`courseIDexam`) REFERENCES `courses` (`courseID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `staffIDexam` FOREIGN KEY (`staffIDexam`) REFERENCES `staffs` (`staffID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

