-- ----------------------------
-- Table structure for members
-- ----------------------------
DROP TABLE IF EXISTS `members`;
CREATE TABLE `members` (
  `memberID` int(10) NOT NULL AUTO_INCREMENT COMMENT '会员编码',
  `phoneNum` varchar(12) NOT NULL COMMENT '手机号码（登陆账号）',
  `password` varchar(15) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '登陆密码',
  `rankM` int(1) DEFAULT '0' COMMENT '会员等级（0普通1白银2黑金）',
  `username` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '用户名',
  `email` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '邮箱',
  `sex` char(2) DEFAULT NULL,
  `birth` date DEFAULT NULL COMMENT '出生日期',
  `addTime` datetime DEFAULT NULL,
  PRIMARY KEY (`memberID`)
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

