-- ----------------------------
-- Table structure for messages
-- ----------------------------
DROP TABLE IF EXISTS `messages`;
CREATE TABLE `messages` (
  `messageID` int(10) NOT NULL AUTO_INCREMENT COMMENT '留言编码',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '留言内容',
  `reply` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '回复内容',
  `publishTime` datetime DEFAULT NULL COMMENT '留言时间',
  `replyTime` datetime DEFAULT NULL COMMENT '回复时间',
  `memIDmes` int(11) DEFAULT NULL COMMENT '会员编码（外键）',
  `staffIDmes` int(11) DEFAULT NULL COMMENT '职员编码（外键）',
  `replyType` int(2) DEFAULT '0' COMMENT '待回复0，已回复1',
  `staffUserName` varchar(10) DEFAULT NULL,
  `memberUserName` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`messageID`),
  KEY `memIDmes` (`memIDmes`),
  KEY `staffIDmes` (`staffIDmes`),
  CONSTRAINT `memIDmes` FOREIGN KEY (`memIDmes`) REFERENCES `members` (`memberID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `staffIDmes` FOREIGN KEY (`staffIDmes`) REFERENCES `staffs` (`staffID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

