-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `newsID` int(10) NOT NULL AUTO_INCREMENT,
  `newsTitle` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '标题',
  `newsContent` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '内容',
  `newsFrom` varchar(100) DEFAULT NULL COMMENT '来源',
  `newsAuthor` varchar(20) DEFAULT NULL COMMENT '作者',
  `newsDescription` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '摘要',
  `publishTime` datetime DEFAULT NULL COMMENT '发布时间',
  `staffIDnews` int(11) DEFAULT NULL COMMENT '操作人员',
  `staffUserName` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL,
  `updateTime` datetime DEFAULT NULL,
  PRIMARY KEY (`newsID`),
  KEY `staffIDnews` (`staffIDnews`),
  CONSTRAINT `staffIDnews` FOREIGN KEY (`staffIDnews`) REFERENCES `staffs` (`staffID`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES ('1', '2020年英语四六级预计什么时候考 英语四级都考什么内容？', '<p><span style=\"font-weight: bold;\">摘要：大学英语四级每年的6月、12月考试，一年有两次考试。考试时间通常情况下为每年6月份、12月份的第三个星期六。2020年上半年四级笔试时间预计在6月13日。</span><br><br>大学英语四级每年的6月、12月考试，一年有两次考试。考试时间通常情况下为每年6月份、12月份的第三个星期六。2020年上半年四级笔试时间预计在6月13日。<br><br>大学英语四级考试，即CET-4，College English Test Band 4的缩写，是由国家教育部高等教育司主持的全国性英语考试。考试的主要对象是根据教育大纲修完大学英语四级的在校专科生、本科生或研究生。大学英语四、六级标准化考试自1986年末开始筹备，1987年正式实施。<br><br>每年考试过后8月份或9月份公布成绩并颁发成绩单，根据教育部规定四六级考试不设置及格线，四级425分(含425分)以上可以报考六级，所以大家普遍认为四六级的合格线为425分。<br><br>英语四六级的分数是排位分，没有总分。<br><br>四级考试由以下四个部分构成：<br><br>听力理解;阅读理解;完型填空或改错;写作和翻译。听力理解部分分值比例为35%;其中听力对话15%，听力短文20%。听力对话部分包括短对话和长对话的听力理解;听力短文部分包括选择题型的短文理解和复合式听写。<br><br>完型填空部分分值比例为10%。完型填空部分采用单项选择题型，改错部分的要求是辨认错误并改正。写作和翻译部分分值比例为20%;其中写作部分(Writing)15%，翻译部分(Translation)5%。写作的体裁包括议论文、说明文、应用文等</p>', '尚七网综合', '小琪琪', '大学英语四级每年的6月、12月考试，一年有两次考试。考试时间通常情况下为每年6月份、12月份的第三个星期六。2020年上半年四级笔试时间预计在6月13日。', '2019-12-21 22:40:10', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('3', '习近平视察澳门政府综合服务中心和英才学校', '<p><melo-data></melo-data></p><div><p><span style=\"font-weight: bold;\">摘要：新华社澳门12月19日电（记者李忠发、朱基钗、郭鑫）19日上午，国家主席习近平在澳门特别行政区行政长官崔世安陪同下，来到位于澳门黑沙环的政府综合服务中心和濠江中学附属英才学校，同澳门市民和师生亲切交流。&nbsp; &nbsp; &nbsp; &nbsp;</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp;新华社澳门12月19日电<span style=\"font-style: italic;\">（记者李忠发、朱基钗、郭鑫）</span>19日上午，国家主席习近平在澳门特别行政区行政长官崔世安陪同下，来到位于澳门黑沙环的政府综合服务中心和濠江中学附属英才学校，同澳门市民和师生亲切交流。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;黑沙环政府综合服务中心于2009年正式投入使用，目前可提供26个政府部门共335项服务。习近平来到服务大楼，在大厅结合展板听取通过整合行政服务资源，为市民提供“一站式”政务服务的情况。在身份证明和社会保障服务专区，习近平察看相关业务办理流程，并同正在办理业务的市民交谈。在24小时身份证明自助服务区，习近平观看护照业务等办理演示，对通过信息科技手段实现社会治理精细化和公共服务高效化的做法表示肯定。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;正在办理业务的市民们纷纷聚拢过来，习近平同他们一一握手、互致问候。市民们对政府惠民措施十分满意，纷纷表达了感恩之情。习近平表示，特别行政区政府为市民提供了很好的服务，体现了以民为本的发展理念。在同大家的交流中，能够感受到澳门同胞生活不断改善，感受到澳门回归20年来发生的历史性变化。他祝愿大家身体健康、生活幸福。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;随后，习近平来到濠江中学附属英才学校。濠江中学创建于1932年，是澳门学生人数最多的基础教育学校，现由校本部即中学部、附属英才学校、小学部和幼稚园部四个校部组成，建校以来始终坚持爱国主义教育，培养了大量优秀爱国爱澳人才。今年“六一”国际儿童节前夕，习近平给该校小学生回信，对他们予以亲切勉励，在澳门社会引起热烈反响。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平来到学校图书馆，听取澳门教育总体情况和学校校史介绍，了解澳门回归以来特别行政区和学校爱国主义教育工作情况。展台上摆放着《中国历史》《品德与公民》等教材，习近平拿起教材仔细翻看。习近平表示，教材是爱国主义教育的重要载体，特别行政区政府同内地合编教材的做法值得充分肯定。要鼓励更多学校使用新教材，让老师们把教材讲好、讲活、深入人心。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平还观看了学校科技创新作品的展示，听取学生介绍科创作品《长城的故事》和导盲杖、夜间智慧泊车系统等，并与学生互动交流。他说，同学们的作品很有创意，令人印象深刻。希望学校不断优化课程设置，探索开拓更有利于培养科技创新人才的教育方式。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;学校礼堂里，濠江中学师生代表，澳门非高等教育学校校长、历史科研组长、德育主任正在观摩以“‘一国两制’与澳门”为题的中国历史公开课。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平走进礼堂，全场起立，热烈鼓掌。习近平向大家挥手致意并就座观摩。公开课结束后，在师生们热烈掌声中，习近平走到学生中间即席发表重要讲话。他说，濠江中学是一所具有悠久爱国传统的学校。今年5月收到英才学校小朋友的来信，浓浓的家国情怀、爱国主义精神跃然纸上，让我很受感动。濠江中学的光荣历史也映照出了广大澳门同胞始终秉持的深厚爱国情怀。爱国主义为澳门贯彻落实“一国两制”打下了坚实的社会政治基础，使“一国两制”实践始终沿着正确的方向前进，结出繁荣昌盛发展的累累硕果。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平表示，今天这堂课意义非凡、有的放矢，抓住了历史的要点和教育的要害。要打牢爱国主义的基础，就要了解历史。作为一个中国人，一定要了解我们民族的历史。“腹有诗书气自华”。14亿中国人民凝聚力这么强，就是因为我们拥有博大精深的中华文化、中华精神，这是我们文化自信的源泉。了解我们5000年延续不绝的历史，就能自然形成强烈的民族自尊心和民族自豪感。了解鸦片战争以来的民族屈辱史，才能理解中国人民对于民族伟大复兴的强烈愿望。今年是新中国成立70周年，我们每个人都为新中国取得的辉煌成就感到自豪，更加坚定了对国家发展道路的信心。澳门回归祖国20年来发生了巨大变化，可谓“天翻地覆慨而慷”，这让澳门同胞更加懂得“一国两制”的根本在于“一国”，更加珍惜今天的良好局面，增强开创美好未来的决心。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平指出，爱国主义教育兹事体大，希望特别行政区政府教育部门和学校担负起主体责任，在已有基础上再接再厉，把爱国主义教育工作做得更好。相信澳门青年人必将成为有为的一代、值得骄傲的一代，成长为澳门和国家的栋梁之才。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp;习近平讲话结束后，全体师生报以热烈的掌声。</p><p></p><p>&nbsp; &nbsp; &nbsp; &nbsp; 离开校园时，师生们围拢过来，向习近平主席热情问好。学生们向习近平赠送了专门制作的《习近平主席回信纪念专刊》，习近平祝同学们学习进步、健康成长，并同大家挥手告别。</p></div>', '新华社', '丁薛祥', '新华社澳门12月19日电（记者李忠发、朱基钗、郭鑫）19日上午，国家主席习近平在澳门特别行政区行政长官崔世安陪同下，来到位于澳门黑沙环的政府综合服务中心和濠江中学附属英才学校，同澳门市民和师生亲切交流。', '2019-12-21 11:24:58', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('4', '教育公安网信部门提醒：擦亮眼睛，识别谣言', '<p><span style=\"font-w\r\neight: bold;\"><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\r\n\"=\"\">摘要：</spanyes\';font-family:宋体;mso-ascii-font-family:calibri;>网上一些不法分子打着“考试包过”“有内部资料”“认识命题教师或考务人员”等噱头，散布网络谣言</span></p><p><spanyes\';font-family:\r\n宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">&nbsp; &nbsp; &nbsp; 中国教育新\r\n闻网讯（记者&nbsp;董鲁皖龙）全国硕士研究生招生考试将于21日举行，临近考试，网上一些不法分子打着“考试包过”“有内部资料”“提供试题答案”“认识命题教师或考务人员”等噱头，散布网络谣言，发布虚假信息，诈\r\n骗考生钱财。为此，教育部门和公安机关、网信部门梳理汇总了近年来一些典型案例，并提醒广大考生：谣言的背后是一些不法商家、网站甚至惯犯精心设计的骗局。广大考生要增强法制观念，提高法律意识，以案为鉴，擦亮眼\r\n睛，明辨谣言，谨防上当受骗。同时，希望各网站不为谣言提供平台，考生不信谣、不传谣。<spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\r\n\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:宋体;mso-ascii-font-family:calib\r\nri;></p><h2><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\r\n\"\">谣言一：花钱可买考研试题和答案？实为“助考”诈骗！</spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></h2><p><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-famil\r\ny:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">&nbsp; &nbsp; &nbsp; &nbsp;每年研考期间都会有不法分子在网上兜售所谓“考研真题”“试题答\r\n案”等，有的甚至包装成社会培训机构，通过网络平台、贴吧、QQ交流群等散布“准确率极高”“违约退款”“预付定金可发试卷样题”等诱惑信息。待考生付款后发现被骗之时，商家早已“人去楼空”。</spanyes\';font-fam\r\nily:宋体;mso-ascii-font-family:calibri;></p><p><spanyes\';font-family:calibri;mso-fareast-font-family:宋体; mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;mso-font-kerning:1.0000pt\r\n;\"=\"\"><span style=\"font-weight: bold;\">【案例】</span><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:\r\n10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">2017年度全国硕士研究生考试期间，广东公安机关破获一起“助考”诈骗案，抓获犯罪嫌疑人2名。据悉，2017年研考前夕，广东公安机关发现有人在网上兜售所谓“考研试题”\r\n，引发网民热议。在掌握确凿证据后，办案民警在广州某网吧将犯罪嫌疑人吴某闯及其同伙莫某敏抓获。经审查，两名犯罪嫌疑人对在网上发布虚假研究生考试答案实施诈骗的犯罪事实供认不讳。据犯罪嫌疑人吴某闯交代，其在\r\n互联网上搜索下载了2017年度硕士研究生考试大纲，自行将大纲编辑为所谓“研究生考试试题”，声称“准确率达80%以上”，以3000元一份的价格卖给了19位考生，非法获利5万多元。<spanyes\';font-family:宋体;mso-ascii-\r\nfont-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-asc\r\nii-font-family:calibri;></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:calibri;mso-fareast-font-family:宋体;></p><p><spanyes\';font-family:宋体;mso-ascii-font-fami\r\nly:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><span style=\"font-weight: bold;\">【提醒】</span>研究生考\r\n试试题属于国家绝密级材料，其命制、运输和保管都有严格的管理和安保措施。广大考生不要心存侥幸心理，不要相信所谓的提前拿到真题和答案。在网上购买所谓“考研真题”“考试答案”，都是骗子发布的虚假信息。购买涉\r\n密材料本身也是违法，切勿尝试。<spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-k\r\nerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></p><p><spanyes\';font-family:宋体;mso-ascii-fon\r\nt-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">《中华人民共和国刑法》明确规定，在法律规定的国家考\r\n试中，“组织作弊的”“为他人提供作弊器材或者其他帮助的”“向他人非法出售或者提供考试的试题、答案的”“代替他人或者让他人代替自己参加考试的”，都属于违法犯罪行为。</spanyes\';font-family:宋体;mso-ascii\r\n-font-family:calibri;>2019年9月4日起施行的《最高人民法院、最高人民检察院关于办理组织考试作弊等刑事案件适用法律若干问题的解释》明确规定：研究生招生考试属于“法律规定的国家考试”；在研究生招生考试中“组\r\n织考试作弊的”“非法出售或者提供考试的试题、答案的”，均应当认定为刑法第二百八十四条规定之一的“情节严重”，将依法从重追究刑事责任。</p><h2><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; m\r\nso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">谣言二：宣称有“内部资料”，结果是一坑到底！</spanyes\';font-family:\r\n宋体;mso-ascii-font-family:calibri;></h2><p><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;\r\n=\"\" mso-font-kerning:1.0000pt;\"=\"\">&nbsp; &nbsp; &nbsp; 在研考备考期间，一些不良商家往往利用考生复习紧张，无暇搜集备考资料，假以“上岸”学长学姐的身份，宣称有考研“内部资料”，实际干的却是骗钱的营生。\r\n上当考生不但钱物两失，还耽误了考研复习进度。<spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;\r\n=\"\" mso-font-kerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></p><p><spanyes\';font-family:宋体\r\n;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><span style=\"font-weight: bold;\">【\r\n案例】</span>2016年，正在准备研究生入学考试的大三学生李同学，偶然间在一个名为“2016某大学考研”群里看见一条消息称：“某大学考研网要举行限定名额的打折活动，各类内部资料低至2-5折出售”。李同学点开链接，\r\n看到非常齐全的考研资料，价格都上千。于是，按照网站客服要求，拍下了一份原价近1000元，现价五折的资料。在付款过程中，群主将承诺的“打五折”变成了“原价购买资料后通过支付宝返回一半的货款”。李同学付款后，\r\n收到的却是粗制滥造的材料，非但未返款还被踢出群。同年，另一高校学生刘同学在一考研论坛中看到一则“2016某大学行政管理整套考研内部资料”的帖子，于是花200元购买。收到后发现，所谓“内部资料”均为百度可查，\r\n再回头联系卖家，却发现早已被拉黑。<spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-fo\r\nnt-kerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></p><p><spanyes\';font-family:宋体;mso-ascii\r\n-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><span style=\"font-weight: bold;\">【提醒】</spa\r\nn>网上涉及研招考试的QQ群、微信群、贴吧、论坛、手机App中各种信息鱼龙混杂，其中不乏兜售考研“内部资料”字样的商业推广信息，这些信息均系不良商家自我标榜的虚假宣传。正常可以公开的考研资料，如考试大纲、参\r\n考书目等，考生均可通过相关官方机构或招生单位研究生招生专门网站进行查询获得，切勿轻信网上叫卖的所谓“内部资料”。<spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri\r\n;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><o:p></o:p></spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></spanyes\';font-family:宋\r\n体;mso-ascii-font-family:calibri;></p><h2><spanyes\';font-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\r\n\" mso-font-kerning:1.0000pt;\"=\"\">谣言三：考前编发“占坑帖”，考后宣传“押中题”</spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></h2><p><spanyes\';font-family:宋体;mso-ascii-font-family:cal\r\nibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\">&nbsp; &nbsp; &nbsp; &nbsp;一些不法分子或机构，为夸大宣传，往\r\n往利用QQ空间或者论坛发帖时间设置机制，通过考前编发“占坑帖”，考后篡改发帖内容，制造“考前押中真题”的假象，进而实施诈骗。</spanyes\';font-family:宋体;mso-ascii-font-family:calibri;></p><p><spanyes\';\r\nfont-family:宋体;mso-ascii-font-family:calibri; mso-hansi-font-family:calibri;mso-bidi-font-family:\'times=\"\" new=\"\" roman\';font-size:10.5000pt;=\"\" mso-font-kerning:1.0000pt;\"=\"\"><span style=\"font-we\r\night: bold;\">【案例】</span>2019年某职业资格考试期间，公安机关破获一起在网上散布疑似考前泄题图片实施诈骗案。经审讯，犯罪嫌疑人为实施诈骗活动，于考前一天在其QQ空间发布一条状态，设置为仅自己可见，在考中\r\n非法获取考试试题后，重新编辑该条状态，将考中制作的所谓“考前押题密卷”图片替换原有内容，并设置为全员可见，状态发布时间仍显示为考前发布时间。通过发布“占坑帖”，谎称可以获取考前押中的真题，实施诈骗，骗\r\n取考生钱财5万余元。与之相似，2017年研招考试刚刚结束，有考生反映某法硕考研论坛涉嫌考前泄露法硕考研试卷真题。经调查，该论坛为做虚假宣传，由工作人员在法硕考试开考前两小时发布“真题帖”，帖内并无内容且设\r\n置为关闭状态。考后通过搜集考生回忆的试题内容，将重新编辑的所谓“法硕考研真题”替换原有内容，并设置为开放状态，发帖时间仍为考前两小时发帖时间。以此编造考前获得真题的假象，误导广大考生。</spanyes\';font\r\n-family:宋体;mso-ascii-font-family:calibri;></p><p><span style=\"font-weight: bold;\">【提醒】</span>近年来，此类涉考犯罪频发，严重扰乱考试秩序，致使广大考生上当受骗。其背后目的，无非是为编造假象实施诈\r\n骗或做虚假宣传谋取利益，蒙蔽考生，误导公众。每年研考前夕，各地教育、公安、网信、工信、市场监管等相关部门都会联合开展考试环境综合治理。对涉考违法违规活动，有关部门将依法严厉打击，发现一起、查处一起，坚\r\n决维护研招考试公平公正。特别提醒广大考生，提高网络安全防范意识，增强识谣辨谣能力，共同抵制涉考网络谣言，净化考试网络生态，营造良好考试环境。</p>', '中国教育新闻网', '董鲁皖龙', '网上一些不法分子打\r\n着“考试包过”“有内部资料”“认识命题教师或考务人员”等噱头，散布网络谣言。', '2019-12-20 20:58:39', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('6', '职业教育“双高计划”建设名单公布', '<p><span style=\"font-weight: bold;\">摘要：教育部、财政部公布中国特色高水平高职学校和专业建设计划建设名单，56所高职学校入选高水平学校建设，141所高职学校入选高水平专业群建设。</span></p><p>本报北京12月18日讯（记者 高靓）今天，教育部、财政部公布中国特色高水平高职学校和专业建设计划建设名单，56所高职学校入选高水平学校建设，141所高职学校入选高水平专业群建设。</p><p>集中力量建设一批具有引领作用的高职学校和专业，大幅提升新时代职业教育现代化水平，是党中央、国务院作出的决策部署。作为《国家职业教育改革实施方案》的重点项目，“双高计划”比肩普通高等教育“双一流”建设，是落实“职业教育与普通教育是两种不同教育类型，具有同等重要地位”的重要制度设计，也是国家职业教育大改革大发展的“先手棋”。</p><p>自今年4月以来，教育部、财政部协力推动项目落地，完成了顶层设计，提出“总量控制、动态管理，年度评价、期满考核，有进有出、优胜劣汰”的建设机制。按照公开、公平、公正原则，经过学校申报、省级推荐、遴选确定三个环节，最终确定第一轮建设学校和专业名单。</p><p>从省份布局看，197所建设单位覆盖了29个省份。从专业布局看，申报的253个专业群覆盖了18个高职专业大类，布点最多的五个专业大类分别是装备制造大类、交通运输大类、电子信息大类、财经商贸大类、农林牧渔大类。从产业布局看，服务面向战略性新兴产业的专业群有75个，面向现代服务业的71个，面向先进制造业的63个，面向现代农业的23个，其他21个。</p><p>教育部职业教育与成人教育司相关负责人表示，“双高计划”实行国家、地方、学校三级推进。国家有关部门负责宏观布局、统筹协调等顶层设计，组建项目建设咨询专家委员会，为项目遴选和建设提供咨询服务。地方加强政策支持和经费保障，协调各方力量支持项目建设，对接区域经济社会发展需求，构建以“双高计划”学校为引领，区域内高职学校协调发展的格局。学校深化改革创新，聚焦建设任务，科学编制建设方案和任务书，健全责任机制，确保建设成效。通过政府行业企业学校多方协同建设，集中力量建设一批引领改革、支撑发展、中国特色、世界水平的高职学校和专业群，打造一批高素质技术技能人才培养培训高地和技术技能创新服务平台，带动职业教育持续深化改革，支撑国家重点产业、区域支柱产业发展。</p><p><br></p>', '教育新闻网-中国教育报', '高靓', '教育部、财政部公布中国特色高水平高职学校和专业建设计划建设名单，56所高职学校入选高水平学校建设，141所高职学校入选高水平专业群建设。', '2019-12-20 12:30:10', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('7', '高等教育能为消除语言障碍做些什么', '<p><span style=\"font-weight: bold;\">摘要：有很多原本应该接受正常教育的孩子，却因为语言障碍，无法与他人正常沟通，无法融入社会，造成了严重的家庭负担和社会问题。以往的研究多从儿童特殊教育的角度探讨语言障碍问题，殊不知，高等院校具有发展科学、培养人才和为社会服务等职能，正可以为解决语言障碍难题提供支持。</span></p><p>有很多原本应该接受正常教育的孩子，却因为语言障碍，无法与他人正常沟通，无法融入社会，造成了严重的家庭负担和社会问题。以往的研究多从儿童特殊教育的角度探讨语言障碍问题，殊不知，高等院校具有发展科学、培养人才和为社会服务等职能，正可以为解决语言障碍难题提供支持。</p><p>语言障碍是指口语、书面语或其他符号系统理解和使用上的损伤。通常情况下，婴儿在出生后只要处在正常语言环境中，语言的发展会遵循一定的年龄轨迹。一部分孩子在母语习得与发展过程中，在语言形式、内容或使用的发展水平上低于其年龄预期，就可以判断为有语言障碍。造成语言障碍的原因非常复杂，可能有神经发育问题、智力问题、听力障碍、构音器官疾病、语言环境不良等因素。语言障碍本身不是一种疾病，而是一种较为常见的障碍。据美国卫生部统计，约9% 的儿童会受到语言障碍的影响。中国香港言语治疗师协会估计，香港10%的儿童患有语言障碍。我国内地人口基数庞大，无论以哪种方式计算，这个数字都是很庞大的。</p><p>语言障碍可以通过干预、治疗得到缓解和矫正，这种治疗通常称为言语治疗。国内言语治疗相较欧美起步较晚，相关的诊断、治疗手段缺乏中国人自己的标准。国际上对于言语治疗师的需求标准是，每10万人中有20名言语治疗师。按此标准，我国至少需要26万名言语治疗师，而我国的职业言语治疗师不足1万人。</p><p>我国关于语言障碍的科学研究刚刚起步，尚不成系统。攻坚语言障碍的语言学、神经科学、医学难题，需要大量学者、医生。高校、医院、科研机构等应该通力合作，对各自擅长领域内急需解决的难题进行攻坚。例如，神经科学、医学领域的学者、专家可以充分利用神经影像学、神经心理学、神经电生理技术和基因测定技术等，探究不同类型语言障碍儿童对应的神经机制。神经语言学家和心理语言学家要配合神经科学家探究语言障碍人群的神经机制；理论语言学家和应用语言学家要开发和建立汉语语言能力常模体系，涵盖语音、音系、词汇、句法、篇章、语用等各个方面，让普通话和方言的语言障碍诊断标准更加有章可循。这些基础研究的成果，可以直接促进语言障碍诊断依据、相关药物和治疗手段、方案的发展。</p><p>言语治疗是一个理论和实践结合得非常紧密的专业，言语治疗师必须充分掌握语言的规律和运用机制，语言同认知神经系统的关系，还有大脑及发音器官的解剖学基础等理论知识，并且必须熟知言语治疗的康复机制，言语治疗与心理治疗、行为治疗和物理治疗的关系等，并有充足的实习课程。因此，社会上功利的培训是无法培养出合格的言语治疗师的，这要求高校把言语治疗设立为一个真正意义上的专业。目前国内有少数医科院校设有与言语治疗相关的本科，但大多是康复专业中的分专业，开设的课程中言语治疗占比不大，而且缺乏语言学方面的专业课程。有些大学以特殊教育的名义开设了与言语治疗相关的专业，但大多缺少言语治疗师的指导，也很少有自己的言语治疗诊所，所以无法开设实践课程。美国以硕士教育为主，英国以本科教育为主。中国香港目前两种做法都有，内地可以考虑从香港引进言语治疗师，作为种子教师。</p><p><span style=\"font-weight: bold;\">作为社会困难群体的语言障碍人群，特别是患有语言障碍的儿童，理应得到全社会的关爱。以自闭症为例，3岁之前是自闭症儿童语言学习的黄金时期，8岁之前是对其进行干预的黄金时期，如果错过了，就耽误了儿童的治疗和矫正的最佳时期。因此，在社会资源有限的情况下，要多向“功能性”倾斜。高校如若对言语治疗这类“功能性”服务做一定程度的倾斜，也能将基础研究所需的受试对象聚集在一起，大大促进基础研究的发展。</span></p><p><br></p>', '中国教育新闻网-中国教育报', '杨洋  石定栩', '有很多原本应该接受正常教育的孩子，却因为语言障碍，无法与他人正常沟通，无法融入社会，造成了严重的家庭负担和社会问题。以往的研究多从儿童特殊教育的角度探讨语言障碍问题，殊不知，高等院校具有发展科学、培养人才和为社会服务等职能，正可以为解决语言障碍难题提供支持。', '2019-12-18 10:09:42', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('8', '怎样办好学前教育专业“课程思政”', '<p><span style=\"font-weight: bold;\">摘要：“三岁看大、七岁看老”。学前教育是教育的开端，是人生教育的“关键期”，相对于其他教育阶段，学前教育对幼儿教师的思想政治素质要求更高。“课程思政”是高校思想政治教育的理念创新，以专业课程为有效教学载体，在“润物无声”的知识教育、能力培养中融入思想价值引领。高等师范院校学前教育专业肩负着培养未来幼儿教师的神圣职责和使命，更要加强专业课与思政课的融合，构筑“课程思政”的立交桥，落实立德树人根本任务。</span><br><br>“三岁看大、七岁看老”。学前教育是教育的开端，是人生教育的“关键期”，相对于其他教育阶段，学前教育对幼儿教师的思想政治素质要求更高。“课程思政”是高校思想政治教育的理念创新，以专业课程为有效教学载体，在“润物无声”的知识教育、能力培养中融入思想价值引领。高等师范院校学前教育专业肩负着培养未来幼儿教师的神圣职责和使命，更要加强专业课与思政课的融合，构筑“课程思政”的立交桥，落实立德树人根本任务。<br><span style=\"font-weight: bold;\">专业课与思政课“两层皮”</span><br>高校是意识形态领域的前沿阵地，各种思想文化交锋，形成了多元、多样、多挑战的复杂环境，这就要求所有教师在履行教书育人职责的同时，主动发挥思想政治教育功能。</p><p><br>学前教育专业本科生抽样调查显示，不少学生选择思政课是“为了应对考试、拿学分或者考研需要”。作为必修课，思想政治理论课具有鲜明的意识形态属性，属于显性教育课程，教育方式更为直接、有效。从学前教育专业课程设置情况看，人才培养方案中80%的课程是专业和素养课程，这是高校思想政治工作不可忽视的课程“阵地”。</p><p><br>但一部分专业课教师在育人、传道方面存在片面性认识。有些人认为，专业课教学是“纯粹”的，只负责专业知识教育和专业能力培养，对于在专业课教学中开展思想政治教育持反对态度。有些人虽然不反对，但由于自身思政理论基础不高，不知如何挖掘和有效融入，硬性做简单加法，没有达到价值引领的效果。有些人认为，过去自己一直在教书育人，对有组织地开展“课程思政”建设不买账，思政融入教学行为零散、随意，不规范、不成体系。这些因素导致专业课教学与思想政治教育存在“两层皮”现象。</p><p><br>习近平总书记主持召开学校思想政治理论课教师座谈会后，全国学校不断深化思政课改革创新，思政课教学百花齐放，质量显著提升。<br>与思政课相比，其他课程的思想政治教育元素是隐性的，是通过其所蕴含的思想道德追求、科学精神、爱国情怀、优秀传统文化、人格培养等内容，通过融入式、嵌入式、渗入式教学对大学生发挥思想价值引领作用。在加强思政课建设的同时，必须树立“课程思政”新理念，充分发掘其他课程和教学方式中蕴含的思想政治教育资源，实现显性教育和隐性教育有机统一，共筑“一体化”育人体系，使各类课程与思想政治理论课同向同行，增强育人合力。</p><p><span style=\"font-weight: bold;\">提升专业课质量的源头活水</span></p><p><span style=\"font-weight: 700;\"><br></span>大学生思想活跃，价值观念多元化倾向明显。特别是学前教育专业学生，很多人定位为将来做幼儿教师，注重学前教育技能课，对唱歌、跳舞、手工、幼儿游戏等实践性较强的课程感兴趣，而对于理论性较强的专业理论课、思政课比较排斥。而当前的理论课教学方法往往不能激发学生的内在情感体验，迫切需要探索更多体验式、互动式的手段与方法，强化思想和价值引领，真正唤醒学生的认同。<br>实施“课程思政”，是有效提升专业课程教学质量的有力抓手。学前教育专业思政教育资源丰富，内容涵盖面广，涉及教育、心理、管理、音乐、美术等领域，可挖掘的育人因素多样，且课程形式新颖。比如“学前教育学”课程蕴含了儿童观、教师观、教育观的基本职业理念；“学前教育政策法规”课程蕴含了法制观念、教育情怀、师德师风、人际交往等隐性教育内容。教师在教授专业知识和专业技能的过程中，可以潜移默化地对学生进行思想价值引领，而不会造成学生的抵触情绪。</p><p><br>“教育者先受教育”。实施“课程思政”有助于专业教师思想政治素养的提升，有助于引导他们把教书和育人、言传和身教结合起来，不断提高育人意识、政治理论水平和人文素养，做到以德立身、以德立学、以德施教。</p><p><br>思政教育与专业教学相辅相成、相得益彰。运用马克思主义的立场、观点、方法开展专业课教学，有助于增强教师理性分析能力、辩证看待问题能力，提高思想的开阔性、深刻性，在专业知识传授、思想价值引领过程中更能关注学生的情感反应，用教师的人格魅力与渊博学识活跃课堂气氛，让学生在行为和情感体验中产生共鸣，让知识的传授更有温度，让理性的教育更有信度，提升教学质量和效果。<br>把“课程思政”提升到中国特色高等教育制度层面来认识</p><p><br>思政课与学前教育专业课之间、不同专业课之间要同向同行，不能“各找各的调、各吹各的号”。必须把“课程思政”建设提升到中国特色高等教育制度层面来认识，坚持系统化思维，确保“课程思政”建设取得明显成效。</p><p><br>首先，做好顶层设计和教师队伍建设。从学校层面，必须建立强有力的领导体制和工作机制，建立系列制度规范和考核激励机制，特别要在人才培养方案制定、教学大纲修订、育人资源开发、教学组织实施、质量评价体系建设等方面强化“思想价值引领”的功能。从教师队伍建设上，应建立和完善全方位、多层次的教师培训体系，搭建学习交流平台，组建各层级研究团队，结合师德师风建设，激励教师主动育人、甘于育人、乐于育人。</p><p><br>其次，充分挖掘专业课程中的思政元素。学前教育专业每项课程中所涉及的历史人物、专业史实、专业案例，都富含思政要素。要把爱国主义精神、社会主义核心价值观、职业道德规范、儿童权益等内容充分融入课程，在坚定学生理想信念、厚植爱国情怀、加强品德修养、增长知识见识、培养奋斗精神和增强综合素质上下功夫，坚持习近平新时代中国特色社会主义思想进教材、进课堂、进头脑，实现铸魂育人。<br>再其次，建立完整的评价机制检验学习成效。高校要建立对课堂教学效果评价的“双效果”评价，既关注学生专业知识技能的学习效果，又关注课堂教学的育人效果。制定合理的激励机制，激励广大专业课教师把立德树人根本任务内化于心，外化于行。建立“课程思政”督导评价体系，确保落实每一个环节，做到稳步推进、行之有效。</p><p><br>教师需要充分了解和掌握大学生的心理特征和思想特点，系统设计思政教育递进教学路径，让实施“课程思政”后的专业课更加接地气，使师生在知识、情感、价值上实现共鸣、共学，在教与学上实现共享、共进。</p>', '中国教育新闻网-中国教育报', '窦岚', '“三岁看大、七岁看老”。学前教育是教育的开端，是人生教育的“关键期”，相对于其他教育阶段，学前教育对幼儿教师的思想政治素质要求更高。“课程思政”是高校思想政治教育的理念创新，以专业课程为有效教学载体，在“润物无声”的知识教育、能力培养中融入思想价值引领。高等师范院校学前教育专业肩负着培养未来幼儿教师的神圣职责和使命，更要加强专业课与思政课的融合，构筑“课程思政”的立交桥，落实立德树人根本任务。', '2019-12-17 15:51:49', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('9', '督导约谈力促研究生教育内涵发展', '<p><span style=\"font-weight: bold;\">摘要：<br>近日，国务院教育督导委员会办公室和北京市教育督导室采用集体约谈方式，分别约谈了一批学位论文抽检问题较多的研究生培养单位，引起了社会各界的广泛关注，起到了传导压力、压实责任、促进质量的预期效果。可以说，督导约谈制度的建立与实施，对于完善研究生教育质量治理体系、提升研究生教育治理能力现代化水平具有积极意义。</span></p><p>近日，国务院教育督导委员会办公室和北京市教育督导室采用集体约谈方式，分别约谈了一批学位论文抽检问题较多的研究生培养单位，引起了社会各界的广泛关注，起到了传导压力、压实责任、促进质量的预期效果。可以说，督导约谈制度的建立与实施，对于完善研究生教育质量治理体系、提升研究生教育治理能力现代化水平具有积极意义。</p><p>《博士硕士学位论文抽检办法》规定，对于已经毕业并授予博士、硕士学位者，由国家组织实施博士学位论文的事后抽检，抽检比例为10%左右；由各省组织实施硕士学位论文的事后抽检，抽检比例为5%左右。对于抽检结果中连续两年出现“存在问题论文”且比例较高或者数量较多的研究生单位，由教育督导部门会同研究生招生、计划、学位等相关部门进行集体约谈。以博士学位论文抽检为例，2018年共授予6.5万个博士学位，随机抽检了博士论文6572篇，共有19家研究生培养单位被约谈。</p><p>上述办法明确规定，学位论文抽检结果是国家进行学位授权点合格评估的重要指标，也是研究生培养单位确定导师招生资格、配置研究生教育资源的重要依据。督导约谈一方面是对《博士硕士学位论文抽检办法》的严格执行与落实，给学位授予单位研究生培养质量敲响警钟；另一方面在研究生教育内涵发展、高质量发展的战略要求下，对于学位授予单位来说更是好事，它倒逼学位授予单位把好研究生教育质量关，从研究生培养各环节切实提高人才培养质量。</p><p>从执行情况来看，被约谈的研究生培养单位都采取了行之有效的整改措施。比如，有的研究生培养单位直接以公函形式告知问题论文毕业生所在工作单位，对问题论文的导师则实施停招、限招、取消导师资格，对于问题论文所在的院系学科进行问责、核减招生计划、加大下一年学位论文答辩前送审比例，通过一系列举措传导压力、压实责任，力争在落小、落细、落实整改要求上下功夫。</p><p>随着我国研究生教育规模的不断扩大，研究生教育的主题主线已逐渐由规模扩张转向内涵发展和高质量发展，立德树人、服务需求、提升质量、追求卓越乃是新时期研究生教育的时代使命和核心任务。研究生培养单位理应坚持育人为本、需求导向、创新引领，在内涵建设、制度规范和质量提升上下功夫，从而办出具有学校特色与国际水准的研究生教育，为研究生教育强国建设和创新型国家建设战略提供人才保障。</p><p><br></p>', '中国教育新闻网-中国教育报', '杜艳秋', '近日，国务院教育督导委员会办公室和北京市教育督导室采用集体约谈方式，分别约谈了一批学位论文抽检问题较多的研究生培养单位，引起了社会各界的广泛关注，起到了传导压力、压实责任、促进质量的预期效果。可以说，督导约谈制度的建立与实施，对于完善研究生教育质量治理体系、提升研究生教育治理能力现代化水平具有积极意义。', '2019-12-17 08:24:10', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('10', '警惕家校沟通中过度使用便捷技术', '<p><span style=\"font-weight: bold;\">摘要：智能手机越来越普及，动动手指即可实现随时、即时、共时的沟通，家校沟通越来越便捷，一对多、多人互动式的各种“群”，成为常规的家校沟通状态。<br></span><br>智能手机越来越普及，动动手指即可实现随时、即时、共时的沟通，家校沟通越来越便捷，一对多、多人互动式的各种“群”，成为常规的家校沟通状态。<br><br>技术便捷的作用是明显的，节约家校沟通的时间和精力成本，提高效益，增进家长对学校工作的了解与支持，还有效地降低了教师工作时间，甚至能使家长对孩子的在校状况了如指掌。但值得警惕的是，家校沟通中的技术优势也可能造成教师和家长双方的过度参与，在教育意义上成为学生发展、学校运行和亲子关系建构的障碍。<br><br>首先，便捷的家校沟通方式有可能使教师、家长双双越位，构成学生全面、健康发展的障碍。在各种类型的家校群中，学校及校外需要完成的任务、要求，学校生活中的特殊事件等，会以最快的速度传递给家长，家长往往高度重视、立刻行动、积极督促甚至越俎代庖。</p><p>家校沟通的重要目的当然是孩子的成长，但家校沟通中最容易被僭越的恰恰也是孩子。在有关教育的所有问题上，永远存在着两个密不可分的方面：成事与成人。家校沟通的直接的、外显目的是成事，内隐的、永恒目的在于成人。因此，家长、教师在享受现代技术的便捷之时，需要三思而后行：什么样的事情需要沟通？需要以什么方式沟通？这样的沟通方式对自己的孩子、对别人的孩子可能产生什么影响？从“成人”的角度思考家校沟通，才可能使我们在享受新技术便利的同时，不丢失育人的初心。</p>', '中国教育新闻网-中国教育报', '袁德润', '智能手机越来越普及，动动手指即可实现随时、即时、共时的沟通，家校沟通越来越便捷，一对多、多人互动式的各种“群”，成为常规的家校沟通状态。', '2019-12-16 22:25:27', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('11', '解决进城务工人员子女教育问题的途径', '<p><span style=\"font-weight: bold;\">摘要：进城务工人员是改革开放的伴生物，是城市化建设的主力军，是城乡一体化建设的重要载体，为中国改革开放的伟大事业作出了杰出贡献。党和国家高度重视进城务工人员，特别是其子女平等接受教育问题，出台一系列政策，切实落实“以流入地政府管理为主，以公办学校接收为主”的“两为主”政策，落实“将常住人口纳入区域教育发展规划，将进城务工人员子女教育纳入财政保障范围”的“两纳入”政策，取得了良好效果。2018年，有17.6万随迁子女在流入地报名参加高考。据计算，这个高考规模是6年前的42.8倍。</span><br><br>进城务工人员是改革开放的伴生物，是城市化建设的主力军，是城乡一体化建设的重要载体，为中国改革开放的伟大事业作出了杰出贡献。党和国家高度重视进城务工人员，特别是其子女平等接受教育问题，出台一系列政策，切实落实“以流入地政府管理为主，以公办学校接收为主”的“两为主”政策，落实“将常住人口纳入区域教育发展规划，将进城务工人员子女教育纳入财政保障范围”的“两纳入”政策，取得了良好效果。2018年，有17.6万随迁子女在流入地报名参加高考。据计算，这个高考规模是6年前的42.8倍。<br><br>当前，进城务工人员子女义务教育问题开始由显性走向隐性，由对起点公平的关注过渡到对过程公平、甚至是结果公平的关注，需要新思路和新方法。<br><br><span style=\"font-weight: bold;\">观念引领</span><br><br>未来30年，我国还将有3亿左右农村劳动力需要转移进城镇，将形成5亿城镇人口、5亿流动迁移人口、5亿农村人口“三分天下”的格局。从这个角度看，解决进城务工人员子女教育问题意义深远，需要树立新观念。同时，在进城务工人员子女教育问题的解决上始终需要观念引领。譬如“两为主”政策的出台和落实过程，就有一个艰难的观念转变过程。时任国家教委副主任柳斌回忆说：“很多地方不情愿，说增加了负担。我就跟他们说，你们看一看，我们国家建了多少高楼大厦、高速公路？但是你们城里人有几个去扛过麻袋，挖过泥沙？为什么不想一想，就是这些民工为我们做的贡献？为什么他们的孩子到这里来，你不给人家创造上学的机会？”“两为主”政策从问题认定、议题创立、制订成文再到具体落实经历了很长时间。之所以得到了很好的落实，也反映了全社会在这个问题上观念得到了彻底转变。<br><br>当前，社会观念对进城务工人员子女教育的直接影响表现在全社会对进城务工人员子女边缘身份的歧视。比如，进城务工人员及其子女在城市里被看作“乡下人”和“乡下人的后代”，这种观念会影响进城务工人员子女的生活和教育。因此，这就要求全社会从正确对待进城务工人员入手，树立全纳理念，坚持民主和平等原则，帮助进城务工人员及其子女很好地融入城市生活。<br><br><span style=\"font-weight: bold;\">制度保障</span><br><br>要促进进城务工人员及其子女很难融入城市生活，必须从户籍制度等的改革入手，打破城乡二元结构。<br><br>2014年7月30日，国务院《关于进一步推进户籍制度改革的意见》正式公布。《意见》明确规定：“进一步调整户口迁移政策，统一城乡户口登记制度，全面实施居住证制度，加快建设和共享国家人口基础信息库，稳步推进义务教育、就业服务、基本养老、基本医疗卫生、住房保障等城镇基本公共服务覆盖全部常住人口。”“努力实现1亿左右农业转移人口和其他常住人口在城镇落户。”2016年9月，北京市关于《进一步推进户籍制度改革的实施意见》正式出台。截至目前，绝大多数省份已出台户籍制度改革方案。<br><br><br><span style=\"font-weight: bold;\">教育干预</span><br><br>教育干预是解决教育过程公平的主要方式。随着进城务工人员子女义务教育问题得到有效解决，进城务工人员子女教育不公平问题逐渐由显性走向隐性的校内不公平，即教育过程不公平成为隐性不公平的主要方面。<br><br>为此，国家也从制度上做了一些干预。如国务院办公厅转发教育部等六部委《关于进一步做好进城务工就业农民子女义务教育工作的意见》，就明确作出了进城务工人员子女与城市学生上学收费“一视同仁”、学习及活动“一视同仁”的规定。但要实现教育过程的公平，重点还需要在学校内部的教育教学行为和管理理念上下功夫。开展融合教育或者多元文化教育，加强城市学生对于进城务工人员子女的认同教育，是有效方式之一。<br><br><span style=\"font-weight: bold;\">技术支撑</span><br><br></p><p>为提高对进城务工人员子女教育问题的认识和重视程度，满足日益增加的教育需求，应全面掌握进城务工人员子女的分布、规模、受教育过程的变动等信息，及时准确把握其教育需求动向，科学预测其分布和发展变化，这有利于流入地政府作出科学规划，特别是对义务教育阶段经费投入、高中阶段教育以及高等教育作出科学规划。</p>', '中国教育新闻网-中国教育报', '张宁娟', '进城务工人员是改革开放的伴生物，是城市化建设的主力军，是城乡一体化建设的重要载体，为中国改革开放的伟大事业作出了杰出贡献。党和国家高度重视进城务工人员，特别是其子女平等接受教育问题，出台一系列政策，切实落实“以流入地政府管理为主，以公办学校接收为主”的“两为主”政策，落实“将常住人口纳入区域教育发展规划，将进城务工人员子女教育纳入财政保障范围”的“两纳入”政策，取得了良好效果。2018年，有17.6万随迁子女在流入地报名参加高考。据计算，这个高考规模是6年前的42.8倍。', '2019-12-15 22:29:41', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('12', '西藏林芝：教育精准扶贫 让义务教育有保障', '<p><span style=\"font-weight: bold;\">摘要：近年来，西藏林芝市在教育脱贫攻坚方面，认真落实建档立卡贫困大学生“三免一补”政策，自开展精准扶贫工作以来，紧紧围绕“义务教育有保障”的目标任务开展工作。</span><br><br>近年来，西藏林芝市在教育脱贫攻坚方面，认真落实建档立卡贫困大学生“三免一补”政策，自开展精准扶贫工作以来，紧紧围绕“义务教育有保障”的目标任务开展工作。<br><br>据了解，林芝全面加强教育资源供给。实行市、县、乡办完小，市、县办初中，市办高中，幼儿园全覆盖的办学理念，共建成各级各类学校210所。小学净入学率为99.96%，初中毛入学率为106.08%，义务教育巩固率达到96.12%。实现了义务教育阶段“有学上、上好学”的目标。<br><br>林芝市积极落实各项学生资助政策。林芝市在校（含民办）农牧民子女、城镇困难家庭子女、全市公路养护段困难家庭子女及公路养护段职工子女均能按标准享受“三包”政策，全市农牧民子女在校（含民办）学生均能按标准享受“农牧区义务教育学生营养改善计划政策”，全市在校（含民办）学生均能按标准享受“义务教育阶段免费教育政策”。实现了农牧民和城镇低收入家庭子女义务教育阶段资助全覆盖。确保了不让一个学生因家庭经济困难而失学，确保了每一个家庭不因学生就学而致贫。<br><br>控辍保学也是林芝市的重点工作。林芝严格履行政府“控辍保学”法定职责，全面落实“控辍保学”四书制，建立健全“控辍保学”协调联动机制，由县（区）人民政府统筹管理、全面负责组织适龄儿童少年入学，切实控制学生流失，将送教上门纳入各县校教学计划，积极开展送教上门工作。目前林芝市残疾儿童少年义务教育入学率已达60%，达到了自治区的目标要求。同时，林芝市特殊学校已建成，计划今年9月开学。按照生源摸排和师资配备情况，初次计划招生50名学生。对重度残疾、无法到校的适龄残疾儿童少年，由各县校按照属地管理原则，继续开展送教上门工作，切实保障其平等接受义务教育的权利。<br><br>同时，林芝积极争取社会力量参与办学。积极争取爱心企业、公益组织和个人投身林芝市教育事业，近几年共建设336个班级图书角，覆盖林芝七县区43所小学。资助家庭困难学生724名。<br></p>', '中国教育新闻网', '蔡继乐 张春铭 周小兰', '近年来，西藏林芝市在教育脱贫攻坚方面，认真落实建档立卡贫困大学生“三免一补”政策，自开展精准扶贫工作以来，紧紧围绕“义务教育有保障”的目标任务开展工作。', '2019-12-14 22:32:23', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('13', '把初中思政课上成学生真心喜爱的课', '<p><span style=\"font-weight: bold;\">摘要：思政课教师要真正成为塑造学生心灵的雕塑家，就要告别自卑、挺起胸膛，执着而自豪地站在讲台上，理直气壮地上好思政课。</span><br><br>2019年3月18日上午，我怀着神圣与光荣的心情走进了庄严的人民大会堂，受邀参加了习近平总书记主持的学校思想政治理论课教师座谈会。作为湖北省唯一发言的教师代表，我作了题为《努力当好学生的引路人》的7分钟发言。当天，在认真聆听完习近平总书记的重要讲话后，170名与会的思想政治理论课教师全体起立，长时间热烈鼓掌。<br><br>作为一名普普通通的初中“道德与法治”课教师，能参加如此高规格而意义重大的座谈会，能代表全国千千万万中学教师表达心声，我感到无上荣光！<br><br>从教26年来，我坚持把党的教育方针贯穿于课程之中，潜心研究教育教学方法，把初中思政课上成学生真心喜欢的课程，使学生在潜移默化中树立坚定的理想信念，热爱中国共产党，热爱祖国，热爱人民。<br><br>让学生从一开始就正确、科学地看待思政课的地位。<br><br>有的人认为，思政课可有可无；有的思政课教师，甚至也自甘边缘化。这些都是认识错误造成的。习近平总书记在座谈会讲话中强调，“思想政治理论课是落实立德树人根本任务的关键课程”“办好思想政治理论课关键在教师”。思政课教师要真正成为塑造学生心灵的雕塑家，就要告别自卑、挺起胸膛，执着而自豪地站在讲台上，理直气壮地上好思政课。同时，还要引导学生转变观念，真正将思政学科作为重要学科来看待。<br><br>每到新学期，我都要郑重地上好“开学第一课”。课堂不传授新知，而是采用情景剧表演、小组讨论、今日说法等方式，通过现实生活中一个个活生生的故事或发人深省的案例，从心理扭曲的刘海洋谈到道德落空的马加爵，从法治意识淡薄的药家鑫到追悔莫及的林森浩，从热衷看星相书的迷茫谈到啃老族的悲哀……引导学生领悟：道德人格上的缺陷贻害终身。从而，一开始就要正确、科学地看待思政学科的地位。</p>', '《人民教育》杂志', '吴又存', '思政课教师要真正成为塑造学生心灵的雕塑家，就要告别自卑、挺起胸膛，执着而自豪地站在讲台上，理直气壮地上好思政课。', '2019-12-21 22:33:41', '1', 'Jetty', null);
INSERT INTO `news` VALUES ('14', '“鸡娃”路线背离了教育正途', '<p><span style=\"font-weight: bold;\">摘要：在社会、经济转型的时代浪潮下，给娃打鸡血并不可怕，关键在于“把娃当娃”，让童年回归本质，摒除过早的功力算计，抛弃空洞的道德说教，一切均按教育的规律办。大家不妨在寓教于乐、在躬身示范、在潜心引领无声滋润之下，让孩子爱上学习，自创、自练、自强成为一个个“小学霸”“小学神”，这可能才是无数“鸡娃”所应思考的正道。</span><br><br>最近，“鸡娃”一词正从补课圈、报班圈的调侃用语，堂而皇之地上升为一种文化现象。所谓“鸡娃”，即指“打鸡血”的孩子，他们不断被父母安排上各种补习班，参加各种等级考，拿各种证书。一线城市中的“鸡娃”现象，几乎成为一种集体焦虑的投射，家长们见面时的寒暄，已经变成：“今天，您鸡娃了吗？”据说，他们之间流传着一个看似荒诞实则写实的段子：“一个4岁的孩子英语单词量1500够不够？”“在美国够了，但在海淀（北京）肯定是不够的！”<br><br>若站在社会经济的角度看，“鸡娃”现象的出现，并非疯狂或焦虑的结果，相反，其几乎是一种理性选择。譬如，从全球的视野来看，欧美同样存在着“鸡娃”现象。据耶鲁大学统计，2012年美国父母陪娃的时间是1976年的3.5倍。也就是说，美国的小孩并不总叫人羡慕，那种无忧无虑的散养式教育，对他们而言，同样是一去不返。那为什么越是发达地区、发达城市，“鸡娃”越是普遍呢？应该说，这主要与教育回报率有关。<br><br>当一个地区的经济相对均衡，由勤奋、智慧所增加的收入，并没有大到可以影响生活质量时，人们去“鸡娃”的动因就很小。家长也好，学校也罢，多会选择相对宽松、自然的教育状态。而一旦整体经济起飞，由知识、技能所带来的收入会成倍、成十倍地拉开差距，甚至由此造成社会阶层的持续分化、转变、跃迁时，教育就成了香饽饽。其对社会、经济、家庭的较高贡献，让市场推动变相竞逐，让文化造就跟风心理，慢慢成为稀缺、优质资源的代表。大城市的生活，由于各项成本开支大，社会阶层悬殊比较明显，这种竞逐性自然就不断升级。<br><br>但教育不是经济的附属物，它有着自身的规律性。人性总是趋利避害的，出现“鸡娃”这一经济和社会现象，一点不奇怪，更不用苛责；但以此来代替教育本身的规律性，无视儿童的发展需求，单以分数、等级、证书等线性指标来“量化”和“打造”孩子，那就走火入魔、误入歧途了。<br><br>实际上，由于发达国家的经济起步较早，他们对“鸡娃”的研究是值得我们借鉴的。德国对教育曾有过一次激烈的讨论，教育应该是以游戏为中心（即“散养式”），还是以学科学习为核心（即“鸡娃式”）？他们比较了50个以游戏为中心的幼儿园与50个以教授知识为中心的幼儿园，并持续跟踪了多年。结果发现，孩子提前学习的优势并没有想象中那么大、那么持久，最多只能保持到小学四年级。从四年级开始，他们的成绩会出现断崖式下滑，特别是在阅读和数学方面劣势更甚，同时，在社交和情感上也出现一定程度的滞后，不如同龄的孩子了。<br><br>这一教训，我们古人早就用拔苗助长给解释过了。人，虽贵为万物之灵，但也和万物一样，都着自己的生命季节。教育的任务，不过是在合适的时间、合适的条件下，给予学习者恰如其分的帮助。我们所担心的，不是“鸡娃”这一现象本身如何，而是它可能造成“鸡娃效应”，误导更多的家长、更多的地区不顾自身实际条件，盲目投入到无休止的教育竞争中，加重孩子和家庭的负担。<br><br>在社会、经济转型的时代浪潮下，给娃打鸡血并不可怕，关键在于“把娃当娃”，让童年回归本质，摒除过早的功力算计，抛弃空洞的道德说教，一切均按教育的规律办。大家不妨在寓教于乐、在躬身示范、在潜心引领无声滋润之下，让孩子爱上学习，自创、自练、自强成为一个个“小学霸”“小学神”，这可能才是无数“鸡娃”所应思考的正道。</p>', '中国教育新闻网-中国教育报', '邱磊', '在社会、经济转型的时代浪潮下，给娃打鸡血并不可怕，关键在于“把娃当娃”，让童年回归本质，摒除过早的功力算计，抛弃空洞的道德说教，一切均按教育的规律办。大家不妨在寓教于乐、在躬身示范、在潜心引领无声滋润之下，让孩子爱上学习，自创、自练、自强成为一个个“小学霸”“小学神”，这可能才是无数“鸡娃”所应思考的正道。', '2019-12-16 22:35:12', '1', 'Jetty', null);

-- ----------------------------
-- Table structure for resources
-- ----------------------------
DROP TABLE IF EXISTS `resources`;
CREATE TABLE `resources` (
  `resID` int(10) NOT NULL AUTO_INCREMENT COMMENT '资源编码',
  `publishTime` datetime NOT NULL,
  `updataTime` datetime DEFAULT NULL,
  `courseIDres` int(11) DEFAULT NULL,
  `staffIDres` int(11) DEFAULT NULL COMMENT '操作人员',
  `resTitle` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '资源标题',
  `resVideo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '视频资源链接',
  `resDoc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '文件资源链接',
  `sectionID` int(2) DEFAULT NULL COMMENT '章节ID',
  `staffUserName` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`resID`),
  KEY `courseID` (`courseIDres`) USING BTREE,
  KEY `staffIDres` (`staffIDres`),
  CONSTRAINT `courseIDres` FOREIGN KEY (`courseIDres`) REFERENCES `courses` (`courseID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `staffIDres` FOREIGN KEY (`staffIDres`) REFERENCES `staffs` (`staffID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=74 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

